				["init"] = {
					["custom"] = "local parent = aura_env.region\nlocal aura_env = aura_env\nlocal f = function(a, b) return a.state.index < b.state.index end\naura_env.sortAndOffset = function()\n    local spacing = 6\n    local count = WeakAuras.IsOptionsOpen() and 1 or 0\n    local t = {}\n    for k, v in pairs(WeakAuras.clones[aura_env.id]) do\n        if v.state and v.state.index and v.state.show then\n            table.insert(t, v)\n        end\n    end\n    table.sort(t, f)\n    \n    for i, region in pairs(t) do\n        if region.toShow then\n            local xOffset = 0\n            local yOffset = 0 - ((region.height + spacing) * count)\n            \n            region:SetAnchor(\"CENTER\" , parent, \"CENTER\")\n            region:SetOffset(xOffset, yOffset)\n            \n            count = count + 1\n        end\n    end\nend\n\n--MELEE / RANGE\n--INSPECT LIB\naura_env.inspect = LibStub:GetLibrary(\"LibGroupInSpecT-1.1\", true)\naura_env.openraid = LibStub:GetLibrary(\"LibOpenRaid-1.0\", true)\naura_env.specToType = {\n    --Death Knight\n    [250] = \"m\", --Blood\n    [251] = \"m\", --Frost\n    [252] = \"m\", --Unholy\n    --Demon Hunter\n    [577] = \"m\", --Havoc\n    [581] = \"m\", --Vengeance\n    --Druid\n    [102] = \"r\", --Balance \n    [103] = \"m\", --Feral \n    [104] = \"m\", --Guardian \n    [105] = \"r\", --Restoration\n    --Hunter\n    [253] = \"r\", --Beast Mastery\n    [254] = \"r\", --Marksmanship\n    [255] = \"m\", --Survival\n    --Mage\n    [62] = \"r\", --Arcane\n    [63] = \"r\", --Fire\n    [64] = \"r\", --Frost\n    --Monk\n    [268] = \"m\", --Brewmaster\n    [270] = \"m\", --Mistweaver\n    [269] = \"m\", --Windwalker\n    --Paladin \n    [65] = \"m\", --Holy  \n    [66] = \"m\", --Protection  \n    [70] = \"m\", --Retribution\n    --Priest   \n    [256] = \"r\", --Discipline \n    [257] = \"r\", --Holy   \n    [258] = \"r\", --Shadow\n    --Rogue   \n    [259] = \"m\", --Assassination  \n    [260] = \"m\", --Outlaw  \n    [261] = \"m\", --Subtlety\n    --Shaman  \n    [262] = \"r\", --Elemental  \n    [263] = \"m\", --Enhancement\n    [264] = \"r\", --Restoration\n    --Warlock  \n    [265] = \"r\", --Affliction \n    [266] = \"r\", --Demonology  \n    [267] = \"r\", --Destruction\n    --Warrior \n    [71] = \"m\", --Arms  \n    [72] = \"m\", --Fury  \n    [73] = \"m\", --Protection\n}\n\naura_env.classToType = {\n    [1] = \"m\", --Warrior\n    [2] = \"m\", --Paladin\n    --[3] = \"\", --Hunter\n    [4] = \"m\", --Rogue\n    [5] = \"r\", --Priest\n    [6] = \"m\", --Death Knight\n    --[7] = \"\", --Shaman\n    [8] = \"r\", --Mage\n    [9] = \"r\", --Warlock\n    [10] = \"m\", --Monk\n    --[11] = \"\", --Druid\n    [12] = \"m\", --Demon Hunter\n}\n\n--NO LIB (SKADA/RECOUNT USERS OMEGAKEK)\naura_env.melee = {\n    [\"TANK\"] = true,\n    [1] = true, --Warrior\n    [2] = true, --Paladin\n    [4] = true, --Rogue\n    [6] = true, --Death Knight\n    [10] = true, --Monk\n    [12] = true, --Demon Hunter\n}\naura_env.mcasts = {\n    [17364] = true, --Stormstrike\n    [5217] = true, --Tiger's Fury\n    [259491] = true, --Serpent Sting\n}\n\naura_env.check = function(unit)\n    if unit then\n        local name, realm = UnitName(unit)\n        if name then\n            local myrealm = GetNormalizedRealmName()\n            local fullname = format(\"%s-%s\", name, realm or myrealm)\n            \n            local class = select(3, UnitClass(unit))\n            local GUID = UnitGUID(unit)\n            local role = UnitGroupRolesAssigned(unit)\n            \n            if aura_env.openraid then\n                local info = aura_env.openraid.playerInfoManager.GetPlayerInfo(name)\n                or aura_env.openraid.playerInfoManager.GetPlayerInfo(fullname)\n                if info and info.specId and aura_env.specToType[info.specId] then\n                    return aura_env.specToType[info.specId], info.specId\n                end\n            end\n            \n            if aura_env.inspect and GUID then\n                local info = aura_env.inspect:GetCachedInfo(GUID)\n                if info and info.global_spec_id and aura_env.specToType[info.global_spec_id] then\n                    return aura_env.specToType[info.global_spec_id], info.global_spec_id\n                end\n            end\n            \n            if VExRT and VExRT.ExCD2 and VExRT.ExCD2.gnGUIDs then\n                local spec = VExRT.ExCD2.gnGUIDs[name] or VExRT.ExCD2.gnGUIDs[fullname]\n                if spec and aura_env.specToType[spec] then\n                    return aura_env.specToType[spec], spec\n                end\n            end\n            \n            if class and aura_env.classToType[class] then\n                return aura_env.classToType[class], 0\n            end\n            \n            if aura_env.melee[class] or aura_env.melee[GUID] or aura_env.melee[role] then\n                return \"m\", 0\n            end\n        end\n    end\nend\naura_env.debuffed = {}\naura_env.count = 1\naura_env.positions = {\n    [1] = \"FRONT LEFT\",\n    [2] = \"FRONT RIGHT\",\n    [3] = \"MIDDLE LEFT\",\n    [4] = \"BACK\",\n    [5] = \"MIDDLE RIGHT\",\n}\n\n",
