   if event == \"DBM_TimerStart\" then\n            if bar then \n                local b = WeakAuras.GetDBMTimerById(bar)\n                if b then\n                    local duration = b.duration\n                    local spellID = b.spellId\n                    if aura_env.config.BigIcon.enableSpellIDFiltering == true then\n                        if aura_env.specifiedSpellID\n                        and aura_env.specifiedSpellID[tonumber(spellID)]  \n                        then\n                            if duration > 5 then\n                                C_Timer.After(duration-5, function() WeakAuras.ScanEvents(\"JODS_TIMER_HIGHLIGHT_RECHECK\", bar,\"DBM\") end)\n                            end\n                            \n                        end\n                    else\n                        if duration > 5 then\n                            C_Timer.After(duration-5, function() WeakAuras.ScanEvents(\"JODS_TIMER_HIGHLIGHT_RECHECK\", bar,\"DBM\") end)\n                        end\n                        \n                    end\n                    \n                end\n                \n                \n            end\n        end\n        \n        if event == \"JODS_TIMER_HIGHLIGHT_MOVEBAR\" then\n            aura_env.flag = true\n        end\n        \n        \n        if event == \"JODS_TIMER_HIGHLIGHT_RECHECK\" then\n            if bar then\n                local b = nil\n                if kek == \"DBM\" then\n                    b = WeakAuras.GetDBMTimerById(bar)\n                    if b then\n                        local duration = b.duration\n                        local expiration = b.expirationTime\n                        local icon = b.icon\n                        local text = b.message\n                        local spellID = b.spellId\n                        \n                        \n                        allstates[bar] = {\n                            show = true,\n                            changed = true,\n     