						["custom"] = "function(allstates, event, ...)\n    if event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local timestamp, subEvent, hideCaster, sourceGUID, sourceName, sourceFlags, sourceRaidFlags, destGUID, destName, destFlags, destRaidFlags, spellID, _,_, auraType = ...\n        if subEvent == \"SPELL_AURA_APPLIED\"\n        and spellID == 368969\n        and aura_env.myAssignment then\n            local _, _, _, _, duration, expirationTime = WA_GetUnitDebuff(destName, spellID)\n            if duration\n            and expirationTime then\n                destName = Ambiguate(destName, \"short\")\n                local currentAssignment\n                local total = 0\n                local myHood\n                for num, player in pairs(aura_env.assignments[aura_env.myAssignment]) do\n                    total = total + 1\n                    if player == destName then\n                        myHood = true\n                        currentAssignment = num    \n                    end\n                end\n                if myHood then\n                    if destName == WeakAuras.me then\n                        SetRaidTarget(destName, aura_env.myAssignment)\n                    end\n                    for k,v in pairs(allstates) do\n                        v.show = false\n                        v.changed = true    \n                    end\n                    local next = total == currentAssignment and aura_env.assignments[aura_env.myAssignment][1] or aura_env.assignments[aura_env.myAssignment][currentAssignment+1]\n                    allstates[destName] = {\n                        show = true,\n                        changed = true,\n                        progressType = \"timed\",\n                        duration = duration,\n                        expirationTime = expirationTime,\n                        current = WA_ClassColorName(destName),\n                        next = string.format(\"Next: %s\", WA_ClassColorName(next)),\n         