						["custom"] = "function(states, event, ...)\n    if event == \"OPTIONS\" then\n        states[\"\"] = {\n            side = aura_env.positions[1],\n            icon = 1354410,\n            name1 = \"Name 1\",\n            name2 = \"Name 2\",\n            player = true,\n            duration = 4,\n            expirationTime = GetTime() + 4,\n            progressType = \"timed\",\n            changed = true,\n            show = true,\n            index = 1,\n        }\n        states[1] = {\n            side = aura_env.positions[2],\n            icon = 1354410,\n            name1 = \"Name 3\",\n            name2 = \"Name 4\",\n            player = false,\n            duration = 4,\n            expirationTime = GetTime() + 4,\n            progressType = \"timed\",\n            changed = true,\n            show = true,\n            index = 2,\n        }\n        local aura_env = aura_env\n        C_Timer.After(0.01, function()\n                aura_env.sortAndOffset()\n        end)\n        return true\n    end\n    \n    if event == \"ENCOUNTER_START\" and ... then\n        aura_env.p3 = false\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" and ... then\n        local _, message, _, srcGUID, srcName, _, _, destGUID, destName, _, _, spellid = ...\n        if message == \"SPELL_CAST_SUCCESS\" then\n            if spellid == 368383 or spellid == 363772 then --Diverted Life Shield / Death Sentence\n                aura_env.p3 = false --disable p3\n            elseif spellid == 363332 then --Unbreaking Grasp\n                aura_env.p3 = true\n            end\n        elseif message == \"SPELL_AURA_APPLIED\" and spellid == 362401 and aura_env.p3 then --Torment\n            local theTime = GetTime()\n            if not aura_env.last or aura_env.last < theTime - 5 then\n                aura_env.count = 1\n                aura_env.last = theTime\n                aura_env.debuffed = {\n                    [1] = {},\n                    [2] = {},\n                }\n                \n                C_Timer.After(0.2, function() WeakAuras.ScanEvents(\"TORMENT_PAIRS\", true) end) \n            end\n            \n            local i = UnitInRaid(destName)\n            local unit = i and \"raid\"..i\n            if unit then\n                local count = aura_env.count%2 == 0 and 2 or 1\n                local name = WA_ClassColorName(unit)\n                local combatType = aura_env.check(unit) or \"r\"\n                table.insert(aura_env.debuffed[count], {name, unit, combatType})\n                aura_env.count = aura_env.count + 1\n            end\n        end\n    elseif event == \"TORMENT_PAIRS\" and ... then\n        table.sort(aura_env.debuffed[1], function(a, b) return a[3] < b[3] end) --melee 1st\n        table.sort(aura_env.debuffed[2], function(a, b) return a[3] < b[3] end) --melee 1st\n        \n        for i, data in ipairs(aura_env.debuffed[1]) do\n            local name, unit = unpack(data)\n            \n            local pairName, pairUnit\n            if aura_env.debuffed[2] and aura_env.debuffed[2][i] then\n                pairName, pairUnit = unpack(aura_env.debuffed[2][i])\n            end\n            \n            local player = UnitIsUnit(\"player\", unit) or pairUnit and UnitIsUnit(\"player\", pairUnit)\n            local icon = 1354410\n            local side = aura_env.positions[i]\n            \n            states[i] = {\n                side = side,\n                icon = icon,\n                name1 = name,\n                name2 = pairName,\n                player = player,\n                duration = 3.8,\n                expirationTime = GetTime() + 3.8,\n                progressType = \"timed\",\n                autoHide = true,\n                changed = true,\n                show = true,\n                index = i,\n            }\n            \n            if player then\n                WeakAuras.ScanEvents(\"TORMENT_ME\", i)\n            end\n        end\n        \n        return true\n    end\nend",
