						["event"] = "Health",
						["customStacks"] = "",
						["customDuration"] = "function()\n    aura_env.dc_ready = GetTime() - aura_env.dc_start >= aura_env.icd_duration\n    if aura_env.dc_up then\n        return aura_env.dc_duration, aura_env.dc_end\n    elseif not aura_env.dc_ready then\n        return aura_env.icd_duration, aura_env.dc_start + aura_env.icd_duration\n    end\nend",
						["customName"] = "",
						["custom"] = "function(event, ...)\n    if event == \"DISCIPLINARY_COMMAND_UPDATE\" then\n        if ... then\n            aura_env.update_schools()\n        end\n        return aura_env.config.show_always\n    end\n    local school_masks = {\n        arcane = 64,\n        fire   = 4,\n        frost  = 16,\n    }\n    local school_order = { \"arcane\", \"frost\", \"fire\" } -- Spells with multiple schools only trigger one school following this priority order. Verified with Spellfire, Frostfire, and an Arcane/Frost/Fire spell.\n    if event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, sourceGUID, _, _, _, _, _, _, _, spellId, _, schoolMask = ...\n        if sourceGUID ~= UnitGUID(\"player\") and subEvent ~= nil then\n            return\n        end\n        if subEvent == \"SPELL_CAST_SUCCESS\" and aura_env.mage_spells[spellId] or aura_env.cleu_event_spell_ids[subEvent] and aura_env.cleu_event_spell_ids[subEvent][spellId] then\n            for _, school in ipairs(school_order) do\n                if bit.band(school_masks[school], schoolMask) > 0 then\n                    aura_env.trigger_school(school)\n                    break -- spells with multiple schools will only trigger one school\n                end\n            end\n        end\n        if subEvent == \"SPELL_AURA_APPLIED\" and spellId == aura_env.dc_spell_id then\n            aura_env.dc_up = true\n            for s, _ in pairs(aura_env.dc_schools) do\n                aura_env.dc_schools[s] = 0\n            end\n            if aura_env.dc_timer then\n                aura_env.dc_timer:Cancel()\n            end\n            aura_env.update_schools()\n            C_Timer.After(aura_env.icd_duration, function() WeakAuras.ScanEvents(\"DISCIPLINARY_COMMAND_UPDATE\") end)\n        elseif subEvent == \"SPELL_AURA_REMOVED\" and spellId == aura_env.dc_spell_id then\n            aura_env.dc_up = false\n            return true\n        end       \n    elseif aura_env.event_triggers[event] then\n        local school = nil\n        for i, arg_filter in pairs(aura_env.event_triggers[event]) do\n            local value = arg_filter[select(i, ...)]\n            if not value then\n                return\n            end\n            if school_masks[value] then\n                school = value\n            end\n        end\n        if school then\n            aura_env.trigger_school(school)\n        end\n    elseif event == \"UNIT_AURA\" and ... == \"player\" then\n        local _, _, _, _, duration, expirationTime = WA_GetUnitBuff(\"player\", aura_env.dc_spell_id)\n        if duration then\n            aura_env.dc_up = true\n            aura_env.dc_duration = duration\n            aura_env.dc_end = expirationTime\n            aura_env.dc_start = expirationTime - duration\n            return true\n        end\n        -- Incanter's Flow triggers DC every time it ticks during combat, just always trigger it\n        -- and don't waste time trying to properly detect the ticks.\n        if WA_GetUnitBuff(\"player\", 116267) and InCombatLockdown() then\n            aura_env.trigger_school(\"arcane\")\n        end\n    end\n    if aura_env.config.show_always then\n        return true\n    end\n    for _, t in pairs(aura_env.dc_schools) do\n        local time_left = (t + aura_env.school_duration) - GetTime()\n        local icd_remains = math.max(0, aura_env.icd_duration - (GetTime() - aura_env.dc_start))\n        if time_left > 0 and icd_remains <= aura_env.config.icd_remains then\n            return true\n        end\n    end\nend",
