				},
				["init"] = {
					["custom"] = "-- Many events other than SPELL_CAST_SUCCESS also represent hidden successful spell casts.\n-- Because Disciplinary Command currently triggers on these hidden casts, add extra events\n-- and corresponding spell IDs here to be able to track them. By default, this just includes\n-- most of the common cases that you can expect to see. Some things will be impossible to track\n-- in this way, such as Flame Patch ticks that hit nothing.\naura_env.cleu_event_spell_ids = {\n    SPELL_HEAL = {\n        [337137] = true, -- Diverted Energy\n    },\n    SPELL_DAMAGE = {\n        [205472] = true, -- Flame Patch\n        [228597] = true, -- Frostbolt\n        [257538] = true, -- Ebonbolt\n        [228600] = true, -- Glacial Spike\n        [228598] = true, -- Ice Lance\n        [84721]  = true, -- Frozen Orb\n        [190357] = true, -- Blizzard\n        [153640] = true, -- Arcane Orb\n    },\n    SPELL_AURA_APPLIED = {\n        [337299] = true, -- Tempest Barrier\n    }\n}\n\n-- Extra school triggers from events other than CLEU. Note that these do not come with school\n-- masks in the events, so the school also needs to be specified.\naura_env.event_triggers = {\n    UNIT_SPELLCAST_SUCCEEDED = {\n        [1] = {\n            player = true, -- match player's unit\n        },\n        [3] = {\n            [7268] = 'arcane', -- Arcane Missiles ticks\n        },\n    },\n}\n\naura_env.dc_spell_id = 327371\naura_env.school_duration = 10\naura_env.icd_duration = 30\n\nlocal aura_env = aura_env\nlocal r = aura_env.region\nlocal prefix = \"DisciplinaryCommand\" .. aura_env.id\naura_env.dc_frames = {}\naura_env.dc_up = false\naura_env.dc_duration = 0\naura_env.dc_end = 0\naura_env.dc_start = 0\naura_env.dc_ready = false\n\nlocal schools = {'arcane', 'fire', 'frost'}\n\n-- The time when each school buff was last triggered. A value of 0 means that the buff was removed.\naura_env.dc_schools = {}\n\nlocal anchor_points = {}\nlocal texture_point = {}\nlocal school_colors = {}\nlocal frame_height = {}\nlocal frame_width = {}\nlocal x_margin = {}\nlocal y_margin = {}\nfor _, s in ipairs(schools) do\n    aura_env.dc_schools[s] = 0\n    anchor_points[s] = {aura_env.config[s].anchor, aura_env.config[s].anchor_to}\n    texture_point[s] = aura_env.config[s].texture_point\n    school_colors[s] = aura_env.config[s].color\n    frame_height[s] = aura_env.config[s].height / 100\n    frame_width[s] = aura_env.config[s].width / 100\n    x_margin[s] = aura_env.config[s].x_margin / 100\n    y_margin[s] = aura_env.config[s].y_margin / 100\nend\n\n-- Create the frames and textures that will be used for each indicator.\nfor s, a in pairs(anchor_points) do\n    local f = _G[prefix .. s] or CreateFrame(\"Frame\", prefix .. s, r)\n    f:SetWidth(r:GetWidth() * frame_width[s])\n    f:SetHeight(r:GetHeight() * frame_height[s])\n    f:ClearAllPoints()\n    f:SetPoint(a[1], r, a[2])\n    f:SetParent(r)\n    if not f.texture then\n        f.texture = f:CreateTexture()\n    end\n    f.texture:SetWidth(f:GetWidth())\n    f.texture:SetColorTexture(unpack(school_colors[s]))\n    f:SetAlpha(r:GetAlpha())\n    f:Hide()\n    aura_env.dc_frames[s] = f\nend\n\n-- Update the visibility and size of each indicator based on the remaining duration of the hidden buff.\naura_env.update_schools = function()\n    if not r:IsShown() then\n        return\n    end\n    for s, f in pairs(aura_env.dc_frames) do\n        f:Hide()\n        local time_left = (aura_env.dc_schools[s] + aura_env.school_duration) - GetTime()\n        local icd_remains = math.max(0, aura_env.icd_duration - (GetTime() - aura_env.dc_start))\n        if time_left > 0 and icd_remains <= aura_env.config.icd_remains then\n            f:Show()\n            local height = f:GetHeight() * (1 - y_margin[s])\n            local width = f:GetWidth() * (1 - x_margin[s])\n            if aura_env.config.update_period > 0 then\n                if aura_env.config[s].animate_height then\n                    height = math.max(1 / f:GetEffectiveScale(), height * time_left / aura_env.school_duration)\n                end\n                if aura_env.config[s].animate_width then\n                    width = math.max(1 / f:GetEffectiveScale(), width * time_left / aura_env.school_duration)\n                end\n            end\n            f.texture:SetHeight(height)\n            f.texture:SetWidth(width)\n            f.texture:ClearAllPoints()\n            f.texture:SetPoint(texture_point[s], f, texture_point[s], f:GetWidth() * x_margin[s] * 0.5, f:GetHeight() * y_margin[s] * 0.5)\n        end\n    end\nend\n\n-- Trigger a hidden school buff and schedules DISCIPLINARY_COMMAND_UPDATE events.\naura_env.trigger_school = function(school)\n    if aura_env.dc_up then\n        return\n    end\n    aura_env.dc_schools[school] = GetTime()\n    local c = 0\n    for _, t in pairs(aura_env.dc_schools) do\n        if GetTime() < t + aura_env.school_duration then\n            c = c + 1\n        end\n    end\n    if aura_env.dc_timer then\n        aura_env.dc_timer:Cancel()\n    end\n    -- If all three buffs are active, they will be expired regardless of the ICD of Disciplinary Command.\n    if c == 3 then\n        for _, s in ipairs(schools) do\n            aura_env.dc_schools[s] = 0\n        end\n    else\n        if aura_env.config.update_period > 0 then\n            aura_env.dc_timer = C_Timer.NewTicker(aura_env.config.update_period, function() WeakAuras.ScanEvents(\"DISCIPLINARY_COMMAND_UPDATE\", true) end, aura_env.school_duration / aura_env.config.update_period)\n        end\n    end\n    aura_env.update_schools()\nend\n\n-- List of all spells with Spell Label 17 (used by Blizzard for Mage spells). This list is unnecessary long, but was automatically generated to ensure that all spells that count will be here. TODO: Add some kind of filtering to the generator to make sure only spells that will ever show up in the combat log are here.\naura_env.mage_spells = {\n    [66] = true, -- Invisibility\n    [1