					["custom"] = "local parent = aura_env.region\nlocal aura_env = aura_env\nlocal f = function(a, b) return a.state.index < b.state.index end\naura_env.sortAndOffset = function()\n    local spacing = 50\n    local t = {}\n    for k, v in pairs(WeakAuras.clones[aura_env.id]) do\n        if v.state and v.state.index and v.state.show then\n            table.insert(t, v)\n        end\n    end\n    table.sort(t, f)\n    \n    for i, region in pairs(t) do\n        if region.toShow then\n            local index = region.state.index\n            \n            local xOffset = 0 - ((region.width + spacing) / 2 ) * 3 + (index * (region.width + spacing))\n            local yOffset = 0 - (region.height + spacing)\n            if i == 4 then\n                xOffset = 0\n                yOffset = 0 - (region.height + spacing) * 2.8\n            elseif i > 2 then\n                xOffset = 0 - ((region.width + spacing) / 2) * 8 + (index * (region.width + spacing))\n                yOffset = 0 - (region.height + spacing) * 2\n            end\n            \n            region:SetAnchor(\"CENTER\" , parent, \"CENTER\")\n            region:SetOffset(xOffset, yOffset)\n        end\n    end\nend\n\naura_env.positions = {\n    [1] = \"FRONT LEFT\",\n    [2] = \"FRONT RIGHT\",\n    [3] = \"MIDDLE LEFT\",\n    [4] = \"BACK\",\n    [5] = \"MIDDLE RIGHT\",\n}\n",
