						["custom"] = "--- ERT TIMERS ICONS FROM NOTES\n\n\nfunction (allstates, event, ...)\n    \n    if (aura_env.config.disableSharedNote and aura_env.config.disablePersonalNote) then\n        return false\n    end\n    \n    local warn = aura_env.config.warn or 15\n    \n    \n    if event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        \n        if (not aura_env.cleucheck) then\n            return false\n        end \n        \n        \n        local _, opt, _, _, caster, _, _, _, target, _, _, spellID = ...\n        \n        \n        local eventTable={SPELL_CAST_SUCCESS=\"SCC\",SPELL_CAST_START=\"SCS\",SPELL_AURA_APPLIED=\"SAA\",SPELL_AURA_REMOVED=\"SAR\"}\n        local thisEvent=eventTable[opt]\n        if (not thisEvent) then return false end\n        local condition=thisEvent..spellID\n        if aura_env.checks[condition] then\n            aura_env.checks[condition] = aura_env.checks[condition] + 1\n            local option_w_counter = thisEvent .. \":\" .. spellID .. \":\" .. aura_env.checks[condition]\n            local option_wo_counter = thisEvent .. \":\" .. spellID .. \":\" .. 0\n            if (aura_env.conditions[option_w_counter] or aura_env.conditions[option_wo_counter]) then\n                \n                \n                caster = caster or \"\"\n                target = target or \"\"\n                local condition_w_counter=aura_env.conditions[option_w_counter]\n                local condition_wo_counter=aura_env.conditions[option_wo_counter]\n                local encounterStarted = aura_env.encounterStarted\n                \n                local id=aura_env.id\n                if (condition_w_counter) then\n                    for i=1, #condition_w_counter do\n                        local k = condition_w_counter[i];\n                        local start = k[\"start\"]\n                        local line = k[\"line\"]\n                        local notetype = k[\"notetype\"]\n                        aura_env.timers[#aura_env.timers + 1] =\n                        C_Timer.After(\n                            start < 0 and 0.1 or start,\n                            function()\n                                WeakAuras.ScanEvents(\"KAZE_ERT_ICONS_EVENT_NEW\", start, notetype, line, encounterStarted, nil,id,caster,target)\n                            end\n                        )\n                    end\n                end\n                if (condition_wo_counter) then\n                    for i=1, #condition_wo_counter do\n                        local k = condition_wo_counter[i];\n                        local start = k[\"start\"]\n                        local line = k[\"line\"]\n                        local notetype = k[\"notetype\"]\n                        aura_env.timers[#aura_env.timers + 1] =\n                        C_Timer.After(\n                            start < 0 and 0.1 or start,\n                            function()\n                                WeakAuras.ScanEvents(\"KAZE_ERT_ICONS_EVENT_NEW\", start, notetype, line, encounterStarted, nil,id,caster,target)\n                            end\n                        )\n                    end\n                end\n            end\n        end\n        \n        \n        \n    elseif event == \"KAZE_ERT_PHASE_EVENT\" \n    and (IsAddOnLoaded(\"ExRT\") or IsAddOnLoaded(\"MRT\")) and not (aura_env.config.disableIconsWhen == 2 and _G.VExRT.Note.enabled == false) and not (aura_env.config.disableIconsWhen == 1 and GMRT.A.Note.frame:IsVisible() == false) and ... then\n        local opt,opt2 = ...\n        local opts = opt..opt2\n        if (not aura_env.checks[opts] or opt2==aura_env.phase) then\n            return false\n        end \n        aura_env.checks[opts] = aura_env.checks[opts] + 1\n        aura_env.phase = opt2\n        local aura_env = aura_env\n        local encounterStarted = aura_env.encounterStarted\n        local conditionPhase = aura_env.conditions[opts]\n        local id=aura_env.id\n        if conditionPhase then\n            for i=1, #conditionPhase do\n                local k = conditionPhase[i]\n                local start = k[\"start\"]\n                local line = k[\"line\"]\n                local notetype = k[\"notetype\"]\n                aura_env.timers[#aura_env.timers + 1] =\n                C_Timer.After(\n                    start < 0 and 0.1 or start,\n                    function()\n                        WeakAuras.ScanEvents(\"KAZE_ERT_ICONS_EVENT_NEW\", start, notetype, line, encounterStarted, opt2,id,\"\",\"\")\n                    end\n                )\n            end\n        end\n        \n    elseif event == \"KAZE_ERT_ICONS_EVENT_NEW\" then\n        local start, notetype, line, encounterStarted, phase, id, caster, target = ...\n        local aura_env = aura_env\n        if encounterStarted == aura_env.encounterStarted\n        and (phase == nil or phase == aura_env.phase)\n        and aura_env.id == id then\n            if start then\n                if start < 0 then\n                    warn = warn + start\n                end\n                \n                \n                for str in string.gmatch(line..\"  \", \"[^ \\n-][^\\n-]-  +\") do\n                    local spellinfo ={\"\",0,0}\n                    \n                    str = str:gsub(\"%%[Tt][Aa][Rr][Gg][Ee][Tt]\",target):gsub(\"%%[Cc][Aa][Ss][Tt][Ee][Rr]\",caster)\n                    \n                    \n                    \n                    \n                    \n                    \n                    local ignoreKeywordFound = false\n                    for k in pairs(aura_env.ignoreKeyword) do\n                        if (str):lower():match(k:lower()) then\n                            ignoreKeywordFound = true\n                            break\n                        end\n                    end\n                    local keywordFound = false\n                    for k in pairs(aura_env.keyword) do\n                        if (str):lower():match(k:lower()) then\n                            keywordFound = true\n                            break\n                        end\n                    end\n                    \n                    \n                    \n                    \n                    \n                    \n                    if\n                    (not ignoreKeywordFound) and\n                    (\n                        keywordFound or \n                        (not aura_env.config.onlyOwnShared and notetype == 1) or \n                        (not aura_env.config.onlyOwnPersonal and notetype == 2))\n                    \n                    then\n                        \n                        \n                        if(str:find(\"{yell}\") and aura_env.config.yell) then\n                            local ispell ={\"\",0,0}\n                            local istr=str:gsub(\"{spell:(%d+):?%d*}\",function(id) return (select(1,GetSpellInfo(id))) end):gsub(\"||c%x%x%x%x%x%x%x%x\", \"\"):gsub(\"||r\", \"\"):gsub(\"({.-})\",function(s) return aura_env.localERTIcons[s] and s or \"\" end )\n                            local iwarn=warn-1\n                            \n                            aura_env.timers[#aura_env.timers + 1] = C_Timer.NewTicker(\n                                1,\n                                function()                                     \n                                    iwarn=iwarn-1\n                                    SendChatMessage(iwarn..\" \"..istr,\"YELL\") \n                                end\n                                ,warn\n                            )\n                        end\n                        \n                        -- Extract icon\n                        str = str:gsub(\"(.*){spell:(%d+):?%d*}\",function(rest,id) spellinfo = {GetSpellInfo(id)} return rest end)\n                        \n                        \n                        local tstr=str:match(\"{[Tt][Ee][Xx][Tt]}(.-){/[Tt][Ee][Xx][Tt]}\")\n                        str=tstr or str\n                        str =str:gsub(\"{spell:(%d+):?%d*}\",aura_env.GSUB_Icon):gsub(\"{icon:([^}]+)}\",\"|T%1:16|t\"):gsub(\"{.-}\",aura_env.localERTIcons):gsub(\"||\", \"|\"):gsub(\"{.-}\",\"\")\n                        \n                        \n                        \n                        \n                        \n                        \n                        allstates[#allstates + 1] = {\n                            show = true,\n                            changed = true,\n                            progressType = \"timed\",\n                            autoHide = true,\n                            duration = aura_env.config.warn or 15,\n                            expirationTime = GetTime() + warn,\n                            name = (spellinfo[1]==\"\" or aura_env.config.showNameWithIcon or tstr) and str or \"\",\n                            spellName = aura_env.config.showSpellnameWithIcon and spellinfo[1] or \"\",\n                            icon = spellinfo[3],\n                            textonly = spellinfo[3]==0,\n                            type = \"timed\"\n                        }\n                        \n                    end\n                end\n                return true\n            end\n        end\n    elseif event == \"ENCOUNTER_START\" and (IsAddOnLoaded(\"ExRT\") or IsAddOnLoaded(\"MRT\")) and not (aura_env.config.disableIconsWhen == 2 and _G.VExRT.Note.enabled == false) and not (aura_env.config.disableIconsWhen == 1 and GMRT.A.Note.frame:IsVisible() == false) and ... then\n        \n        \n        aura_env.timers = {}\n        aura_env.encounterStarted = GetTime()\n        aura_env.phase = 1\n        aura_env.conditions = {}\n        aura_env.checks = {}\n        aura_env.cleucheck=false\n        \n        aura_env.keyword[\"group:\"..aura_env.MyGroup()] = true\n        aura_env.keyword[\"class:\"..select(2, UnitClass(\"player\"))] = true\n        \n        \n        local notes = {not aura_env.config.disableSharedNote and _G.VExRT.Note.Text1 or \"\",not aura_env.config.disablePersonalNote and _G.VExRT.Note.SelfText or \"\"}\n        \n        \n        \n        for i = 1, 2 do\n            local text = notes[i]\n            \n            \n            for line in text:gmatch(\"[^\\r\\n]+\") do\n                \n                --filters stolen from MRT\n                local spec = GetSpecialization()\n                if spec then\n                    local _,spec,_,_,role,_ = GetSpecializationInfo(spec)\n                    \n                    \n                    aura_env.keyword[\"role:\"..role] = true\n                    aura_env.keyword[\"spec:\"..spec] = true\n                    \n                    if role ~= \"HEALER\" then line = line:gsub(\"{[Hh]}.-{/[Hh]}\",\"\") end\n                    if role ~= \"TANK\" then line = line:gsub(\"{[Tt]}.-{/[Tt]}\",\"\") end\n                    if role ~= \"DAMAGER\" then line = line:gsub(\"{[Dd]}.-{/[Dd]}\",\"\") end\n                end\n                line = line:gsub(\"{(!?)[Gg](%d+)}(.-){/[Gg]}\",aura_env.GSUB_Group):gsub(\"{(!?)[Pp]:([^}]+)}(.-){/[Pp]}\",aura_env.GSUB_Player):gsub(\"{(!?)[Cc]:([^}]+)}(.-){/[Cc]}\",aura_env.GSUB_Class):gsub(\"{(!?)[Rr][Aa][Cc][Ee]:([^}]+)}(.-){/[Rr][Aa][Cc][Ee]}\",aura_env.GSUB_Race):gsub(\"[^ \\n,%(%)%[%]_%$#@!&]+\",aura_env.GSUB_AutoColor_Data)\n                \n                \n                \n                local keywordFound = false\n                for k in pairs(aura_env.keyword) do\n                    if line:lower():match(k:lower()) then\n                        keywordFound = true\n                        break\n                    end\n                end\n                \n                \n                \n                if (\n                    (not aura_env.config.onlyOwnShared and i == 1)\n                    or (not aura_env.config.onlyOwnPersonal and i == 2)\n                or keywordFound)\n                then        \n                    \n                    \n                    \n                    local minute, sec, options = line:match(\"{time:(%d+)[:%.]?(%d*),?([^{}]*)}\")     \n                    \n                    \n                    if (sec == nil or sec == \"\") then\n                        sec = tonumber(minute)\n                        minute = 0\n                    else\n                        minute = tonumber(minute)\n                        sec = tonumber(sec)\n                    end\n                    \n                    if sec then\n                        local start = sec + (minute * 60) - warn\n                        \n                        if (options == nil or options == \"\" or options == \"p1\") then\n                            local encounterStarted = aura_env.encounterStarted\n                            local phase = (options == \"p1\") and 1 or nil\n                            local id = aura_env.id\n                            aura_env.timers[#aura_env.timers + 1] =\n                            C_Timer.After(\n                                start < 0 and 0.1 or start,\n                                function()\n                                    WeakAuras.ScanEvents(\"KAZE_ERT_ICONS_EVENT_NEW\", start, i, line, encounterStarted, phase,id,\"\",\"\")\n                                end\n                            )\n                        end\n                        if (not (options==\"\")) and options then\n                            local opt, spellID, count = strsplit(\":\", options)\n                            spellID = spellID or \"\"\n                            if (not aura_env.cleucheck) then\n                                if (opt == \"SCC\" or opt == \"SCS\" or opt == \"SAA\" or opt == \"SAR\") then\n                                    aura_env.cleucheck = true\n                                end                            \n                            end\n                            aura_env.conditions[options] = aura_env.conditions[options] or {}\n                            aura_env.checks[opt .. spellID] = aura_env.checks[opt .. spellID] or 0\n                            table.insert(\n                                aura_env.conditions[options],\n                                {\n                                    start = start,\n                                    notetype = i,\n                                    line = line\n                                }\n                            )\n                        end\n                    end\n                end\n            end\n        end\n        \n    elseif event == \"ENCOUNTER_END\" then\n        \n        aura_env.conditions = {}\n        aura_env.checks = {}\n        aura_env.cleucheck = false\n        for i=1, #allstates do\n            local v = allstates[i]\n            v.show = false\n            v.changed = true\n        end\n        if aura_env.timers then\n            for i=1, #aura_env.timers do\n                local v = aura_env.timers[i]\n                v:Cancel()\n                aura_env.timers[i] = nil\n            end\n        end\n    end\n    \n    return true\nend",
