				["init"] = {
					["custom"] = "\ndo\n    aura_env.order = {\n        177891,    -- Summoner\n        177889,    -- Souljudg\n        177892,    -- Goliath\n        175456,\n        174568, \n        174592,\n        170910\n    }\n    \n    aura_env.index = {}\n    aura_env.count = {}\n    for priority, npcId in ipairs(aura_env.order) do\n        aura_env.index[npcId] = priority\n        aura_env.count[npcId] = 0\n    end\n    \n    aura_env.priority = #aura_env.order\n    \n    aura_env.GetGUID = function(unit)\n        local guid = UnitGUID(unit)\n        if guid then\n            local npcId = select(6, strsplit(\"-\", guid))\n            return guid, tonumber(npcId)\n        end\n        return guid\n    end\nend\n\n\n\n",
