						["custom"] = "function(allstates, event, unit)\n    local countUpdated\n    if event == \"NAME_PLATE_UNIT_ADDED\" then\n        \n        if UnitExists(unit) then\n            \n            local guid, npcId = aura_env.GetGUID(unit)\n            local priority = aura_env.index[npcId]\n            if priority then\n                allstates[guid] = allstates[guid] or {}\n                local state = allstates[guid]\n                state.changed = true\n                state.show = true\n                state.progressType = \"static\"\n                state.name = priority\n                state.unit = unit\n                state.npcId = npcId\n                aura_env.count[npcId] = aura_env.count[npcId] + 1\n                countUpdated = true\n                \n            end\n        end\n    end\n    if event == \"NAME_PLATE_UNIT_REMOVED\" then\n        if UnitExists(unit) then\n            local guid, npcId = aura_env.GetGUID(unit)\n            local state = allstates[guid]\n            if state then\n                state.changed = true\n                state.show = false\n                local count = aura_env.count[npcId] - 1\n                aura_env.count[npcId] = (count < 0) and 0 or count\n                countUpdated = true\n            end\n        end\n    end\n    if countUpdated then\n        for priority, npcId in ipairs(aura_env.order) do\n            local count = aura_env.count[npcId]\n            if count > 0 then\n                aura_env.priority = priority\n                break\n            end\n        end\n    end\n    return true\nend",
