\n        if myPriority == 10 and myPriority == othersPriority then\n            --pingpong strat\n            allstates[\"PP\"] = {\n                show = true,\n                changed = true,\n                name = myDuration == othersDuration and aura_env.assign[myCounter] or myDuration < othersDuration and \"GO FAR\" or \"GO CLOSE\"\n            }\n            return true\n        else\n            --massDispel strat\n            allstates[\"MD\"] = {\n                show = true,\n                changed = true,\n                name = myPriority == othersPriority and aura_env.assign[myCounter] or myPriority < othersPriority and \"GO FAR\" or \"GO CLOSE\"\n            }\n            return true\n        end\n    end\nend",
