						["custom"] = "function(allstates,event,bar,kek)\n    \n    if aura_env.config.BigIcon.EnableBigIcon == true then\n        \n        if event == \"ENCOUNTER_START\" then\n            aura_env.counter = {}\n        end\n        if event == \"BigWigs_StartBar\" then\n            if bar then\n                local b = WeakAuras.GetBigWigsTimerById(bar)\n                if b then\n                    local duration = b.duration\n                    local spellID = b.spellId\n                    if aura_env.config.BigIcon.enableSpellIDFiltering == true then\n                        if aura_env.specifiedSpellID\n                        and aura_env.specifiedSpellID[tonumber(spellID)] \n                        then \n                            if duration > 5 then\n                                C_Timer.After(duration-5, function() WeakAuras.ScanEvents(\"JODS_TIMER_HIGHLIGHT_RECHECK\", bar,\"BW\") end)  \n                            end\n                            \n                            \n                        end\n                    else \n                        \n                        if duration > 5 then\n                            C_Timer.After(duration-5, function() WeakAuras.ScanEvents(\"JODS_TIMER_HIGHLIGHT_RECHECK\", bar,\"BW\") end)  \n                        end\n                        \n                    end\n                    \n                end\n                \n                \n                \n            end\n        end\n        if event == \"BigWigs_StopBar\" then\n            local state = allstates[bar] \n            if state then\n                \n                state.show = false\n                state.changed = true\n            end\n        end\n        \n        if event == \"DBM_TimerStop\" then\n            local state = allstates[bar] \n            \n            if state then\n                \n                state.show = false\n                state.changed = true\n            end\n        end\n        \n        if event == \"DBM_TimerStart\" then\n            if bar then \n                local b = WeakAuras.GetDBMTimerById(bar)\n                if b then\n                    local duration = b.duration\n                    local spellID = b.spellId\n                    if aura_env.config.BigIcon.enableSpellIDFiltering == true then\n                        if aura_env.specifiedSpellID\n                        and aura_env.specifiedSpellID[tonumber(spellID)]  \n                        then\n                            if duration > 5 then\n                                C_Timer.After(duration-5, function() WeakAuras.ScanEvents(\"JODS_TIMER_HIGHLIGHT_RECHECK\", bar,\"DBM\") end)\n                            end\n                            \n                        end\n                    else\n                        if duration > 5 then\n                            C_Timer.After(duration-5, function() WeakAuras.ScanEvents(\"JODS_TIMER_HIGHLIGHT_RECHECK\", bar,\"DBM\") end)\n                        end\n                        \n                    end\n                    \n                end\n                \n                \n            end\n        end\n        \n        if event == \"JODS_TIMER_HIGHLIGHT_MOVEBAR\" then\n            aura_env.flag = true\n        end\n        \n        \n        if event == \"JODS_TIMER_HIGHLIGHT_RECHECK\" then\n            if bar then\n                local b = nil\n                if kek == \"DBM\" then\n                    b = WeakAuras.GetDBMTimerById(bar)\n                    if b then\n                        local duration = b.duration\n                        local expiration = b.expirationTime\n                        local icon = b.icon\n                        local text = b.message\n                        local spellID = b.spellId\n                        \n                        \n                        allstates[bar] = {\n                            show = true,\n                            changed = true,\n                            progressType = \"timed\",\n                            duration = expiration-GetTime(),\n                            expirationTime = expiration,\n                            icon = icon,\n                            barName  = text,\n                            spellID = spellID,\n                            autoHide = true,\n                            resort = true,\n                        }\n                        aura_env.flag = true\n                        \n                        C_Timer.After(duration-5, function() WeakAuras.ScanEvents(\"JODS_TIMER_HIGHLIGHT_MOVEBAR\", bar) end)\n                    end\n                end\n                \n                if kek == \"BW\" then\n                    b = WeakAuras.GetBigWigsTimerById(bar)\n                    if b then\n                        local duration = b.expirationTime-GetTime()\n                        local expiration = b.expirationTime\n                        local icon = b.icon\n                        local text = b.text\n                        local spellID = b.spellId\n                        local isCast = b.cast\n                        local emphasized = b.emphasized\n                        \n                        if (not aura_env.config.BigIcon.excludeCast or aura_env.config.BigIcon.excludeCast and not isCast) then\n                            local output = text\n                            if not isCast then\n                                output = string.gsub(text, \"%s%(%d+%)\", \"\")\n                                if not aura_env.counter[output] then\n                                    aura_env.counter[output] = 1\n                                else aura_env.counter[output] = aura_env.counter[output] + 1\n                                end\n                            else\n                                output = text\n                            end\n                            \n                            if duration <6 and duration >0 then\n                                allstates[bar] = {\n                                    show = true,\n                                    changed = true,\n                                    progressType = \"timed\",\n                                    duration = expiration-GetTime(),\n                                    expirationTime = expiration,\n                                    icon = icon,\n                                    barName  = output,\n                                    emphasized = emphasized,\n                                    isCast = isCast,\n                                    spellID = spellID,\n                                    count = aura_env.counter[output] or 0,\n                                    autoHide = true,\n                                    resort = true,\n                                }\n                                aura_env.flag = true\n                                if duration-5 >0 then\n                                    C_Timer.After(duration-5, function() WeakAuras.ScanEvents(\"JODS_TIMER_HIGHLIGHT_MOVEBAR\", bar) end)\n                                end\n                                \n                            end\n                        end\n                        \n                    end\n                end\n                \n            end\n        end\n        \n        \n        return true\n    end\n    \nend",
